import React from 'react';
import { useParams } from 'react-router-dom';

interface Character {
  name: string;
  relatedCharacters: string[];
  events: string[];
  funFacts: string[];
}

interface CharacterPageProps {
  characters: Character[];
}

const CharacterPage: React.FC<CharacterPageProps> = ({ characters }) => {
  const { name } = useParams<{ name: string }>();
  const character = characters.find((char) => char.name === name);

  if (!character) {
    return <div>Character not found</div>;
  }

  return (
    <div className="character-page">
      <h2>{character.name}</h2>
      <h3>Related Characters</h3>
      <ul>
        {character.relatedCharacters.map((relChar, index) => (
          <li key={index}>{relChar}</li>
        ))}
      </ul>
      <h3>Events</h3>
      <ul>
        {character.events.map((event, index) => (
          <li key={index}>{event}</li>
        ))}
      </ul>
      <h3>Fun Facts</h3>
      <ul>
        {character.funFacts.map((fact, index) => (
          <li key={index}>{fact}</li>
        ))}
      </ul>
    </div>
  );
};

export default CharacterPage;
